query:
-- Get the Male ward department ID
SET @maleWardId = (SELECT id FROM departments WHERE name = 'Male ward' LIMIT 1);

-- Update all categories to be assigned to Male ward
UPDATE kpi_categories SET departmentId = @maleWardId WHERE departmentId = 0;

-- Update all indicators to be assigned to Male ward
UPDATE kpi_indicators SET departmentId = @maleWardId WHERE departmentId = 0;

-- Verify the updates
SELECT COUNT(*) as categories_updated FROM kpi_categories WHERE departmentId = @maleWardId;
SELECT COUNT(*) as indicators_updated FROM kpi_indicators WHERE departmentId = @maleWardId;

