-- Get the user and department IDs
SET @userId = (SELECT id FROM users LIMIT 1);
SET @deptId = (SELECT id FROM departments WHERE name = 'Male ward' LIMIT 1);

-- Insert categories
INSERT INTO kpi_categories (userId, name, description, sortOrder, requiresPatientInfo, createdAt, updatedAt)
VALUES 
  (@userId, 'Mandatory', 'Mandatory safety indicators', 1, 1, NOW(), NOW()),
  (@userId, 'Respiratory', 'Respiratory care indicators', 2, 1, NOW(), NOW()),
  (@userId, 'Renal', 'Renal care indicators', 3, 0, NOW(), NOW());

-- Get category IDs
SET @mandatoryId = (SELECT id FROM kpi_categories WHERE userId = @userId AND name = 'Mandatory' LIMIT 1);
SET @respiratoryId = (SELECT id FROM kpi_categories WHERE userId = @userId AND name = 'Respiratory' LIMIT 1);
SET @renalId = (SELECT id FROM kpi_categories WHERE userId = @userId AND name = 'Renal' LIMIT 1);

-- Insert indicators
INSERT INTO kpi_indicators (userId, categoryId, name, description, unit, sortOrder, requiresPatientInfo, createdAt, updatedAt)
VALUES 
  (@userId, @mandatoryId, 'Pressure Sore', 'Number of pressure sore incidents', 'cases', 1, 1, NOW(), NOW()),
  (@userId, @mandatoryId, 'Fall Incidents', 'Number of patient fall incidents', 'cases', 2, 1, NOW(), NOW()),
  (@userId, @respiratoryId, 'NIV Cases', 'Non-invasive ventilation cases', 'cases', 1, 1, NOW(), NOW()),
  (@userId, @respiratoryId, 'Intubated Cases', 'Intubation cases', 'cases', 2, 1, NOW(), NOW()),
  (@userId, @renalId, 'RDU Sessions', 'Renal dialysis unit sessions', 'sessions', 1, 0, NOW(), NOW());

